package selfProgramming;

public class Loop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		 do {	
			   if ((i % 2)== 0)
				System.out.println("0 reminder = "+i);
			   else
				   System.out.println("Else reminder = "+i);
			   i++;
		    } while(i<5);

	}

}
